"use strict";!function(){const e=document.querySelector("form");e.addEventListener("submit",function(t){t.preventDefault();let s=e.getElementsByClassName(".alert");for(let e=0;e<s.length;e++)s[e].classList.remove("active");let n=new XMLHttpRequest,a=e.getAttribute("action"),l=document.getElementById("name"),o=document.getElementById("email"),c=document.getElementById("message");a+=`?name=${l.value}&email=${o.value}&message=${c.value}`,n.open("GET",encodeURI(a)),n.setRequestHeader("Content-Type","application/x-www-form-urlencoded"),n.onload=function(){if(200===n.status)e.querySelector(".success").classList.add("active"),e.querySelector(".error").classList.remove("active"),l.value="",o.value="",c.value="";else{let t=e.querySelector(".error");t.textContent=n.responseText,t.classList.add("active")}},n.send()})}();